<?phpuse App\Auth\Auth;if(!empty($_POST["register"])) {    /* Form Required Field Validation */    foreach($_POST as $key=>$value) {        if(empty($_POST[$key])) {            $error_message = "All Fields are required";            break;        }    }    /* Password Matching Validation */    if($_POST['password'] != $_POST['confirm_password']){        $error_message = 'Passwords should be same<br>';    }    /* Email Validation */    if(!isset($error_message)) {        if (!filter_var($_POST["email"], FILTER_VALIDATE_EMAIL)) {            $error_message = "Invalid Email Address";        }    }    if(!isset($error_message)) {        try {            $pdo = new PDO('mysql:host=localhost;dbname=amarproshno_db', 'root', '');            $query = 'SELECT * FROM users WHERE email = :email OR username = :username';            $stmt = $pdo->prepare($query);            $data = array(                ':email' => $_POST["email"],                ':username' => $_POST["username"],            );            $stmt->execute($data);            $result = $stmt->fetch(PDO::FETCH_ASSOC);            if($result['username']==$_POST["username"]) {                $error_message  = "sorry username already taken !";            }            else if($result['email']==$_POST["email"]) {                $error_message  = "sorry email id already taken !";            }else{                $register = new Auth();                $register->setData($_POST)->register();            }        } catch(PDOException $e) {            echo 'ERROR: ' . $e->getMessage();        }    }}?><!-- =-=-=-=-=-=-= Main Area =-=-=-=-=-=-= -->  <div class="main-content-area">    <!-- =-=-=-=-=-=-= Register Form =-=-=-=-=-=-= -->    <section class="section-padding-80 white" id="register">      <div class="container">        <div class="row">          <div class="col-sm-6 col-sm-offset-3 col-md-6 col-md-offset-3">              <?php if(!empty($success_message)) { ?>                  <div class="success-message"><?php if(isset($success_message)) echo $success_message; ?></div>              <?php } ?>              <?php if(!empty($error_message)) { ?>                  <div class="error-message"><?php if(isset($error_message)) echo $error_message; ?></div>              <?php } ?>            <div class="box-panel">              <!-- form login -->              <form action="" method="post">                <div class="form-group">                  <label>User Name</label>                  <input type="text" placeholder="Your User Name" class="form-control" name="username" value="<?php if(isset($_POST['username'])) echo $_POST['username']; ?>">                </div>                <div class="form-group">                  <label>Email</label>                  <input type="email" placeholder="Your Email" class="form-control" name="email" value="<?php if(isset($_POST['email'])) echo $_POST['email']; ?>">                </div>                <div class="form-group">                  <label>Password</label>                  <input type="password" placeholder="Your Password" class="form-control" name="password">                </div>                <div class="form-group">                  <label>Confirm Password</label>                  <input type="password" placeholder="Verify Your Password" class="form-control" name="confirm_password">                </div>                <div class="form-group">                  <div class="row">                    <div class="col-xs-12 text-right">                      <p class="help-block"><a  href="<?php echo SITE_URL ; ?>/page/login">Already Register Sing In</a>                      </p>                    </div>                  </div>                </div>                <input class="btn btn-primary btn-lg btn-block" type="submit" value="Create Account" name="register">              </form>              <!-- form login -->            </div>          </div>          <div class="clearfix"></div>        </div>      </div>      <!-- end container -->    </section>    <!-- =-=-=-=-=-=-= Register Form End =-=-=-=-=-=-= -->  </div>  <!-- =-=-=-=-=-=-= Main Area End =-=-=-=-=-=-= -->